    private static List<List<string>> ConvertToMatrix(List<string> inputStrings)
    {
        List<List<string>> matrix = new();
        foreach (string line in inputStrings)
        {
            List<string> row = new();
            foreach (char ch in line)
            {
                row.Add(ch.ToString());
            }
            matrix.Add(row);
        }
        return matrix;
    }

    private static int CountWordOccurrences(List<List<string>> matrix, string word)
    {
        int rows = matrix.Count;
        int cols = matrix[0].Count;
        int count = 0;

        for (int i = 0; i < rows; i++)
        {
            for (int j = 0; j < cols; j++)
            {
                // Check all 8 possible directions
                count += CheckDirection(matrix, i, j, word, 0, 1);   // Horizontal right
                count += CheckDirection(matrix, i, j, word, 0, -1);  // Horizontal left
                count += CheckDirection(matrix, i, j, word, 1, 0);   // Vertical down
                count += CheckDirection(matrix, i, j, word, -1, 0);  // Vertical up
                count += CheckDirection(matrix, i, j, word, 1, 1);   // Diagonal down-right
                count += CheckDirection(matrix, i, j, word, 1, -1);  // Diagonal down-left
                count += CheckDirection(matrix, i, j, word, -1, 1);  // Diagonal up-right
                count += CheckDirection(matrix, i, j, word, -1, -1); // Diagonal up-left
            }
        }
        return count;
    }

    private static int CheckDirection(List<List<string>> matrix, int row, int col, string word, int rowDir, int colDir)
    {
        int rows = matrix.Count;
        int cols = matrix[0].Count;

        for (int k = 0; k < word.Length; k++)
        {
            int newRow = row + (k * rowDir);
            int newCol = col + (k * colDir);

            if (newRow < 0 || newRow >= rows || newCol < 0 || newCol >= cols || matrix[newRow][newCol] != word[k].ToString())
            {
                return 0;
            }
        }
        return 1;
    }
